mapper.update:
update cscenter set cs_writer = #{cs_writer},
cs_title = #{cs_title},
cs_content = #{cs_content}, cs_date = sysdate WHERE cs_idx = #{cs_idx}

mapper.select:
SELECT *
FROM (

SELECT ROWNUM RN, A.*
FROM (
SELECT *
FROM cscenter
ORDER BY cs_idx DESC
) A
)
WHERE RN BETWEEN #{start} AND #{end}
